import React, { useState, useEffect } from "react";
import axios from "axios";
import Todo from "../Todo";
import PopupDelete from "../common/PopupDelete";

function Todolist() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    axios.get("http://localhost:8080/todos").then((res) => setTodos(res.data));
    if (todos == null) {
      console.log("todo is null");
      return <></>;
    } else {
      console.log(todos);
    }
  }, []);

  const addTodo = () => {
    axios
      .post("http://localhost:8080/todos", { title, status: false })
      .then((res) => {
        setTodos([...todos, res.data]);
        setTitle("");
      });
  };

  const toggleStatus = (id, status) => {
    axios
      .put(`http://localhost:8080/todos/${id}`, { status: !status })
      .then(() => {
        setTodos(
          todos.map((todo) =>
            todo.id === id ? { ...todo, status: !status } : todo
          )
        );
      });
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:8080/todos/${id}`).then(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    });
  };

  // const myModal = document.getElementById("myModal");
  // const myInput = document.getElementById("myInput");

  // myModal.addEventListener("shown.bs.modal", () => {
  //   myInput.focus();
  // });

  return (
    <>

      <div
        class="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h1 class="modal-title fs-5" id="staticBackdropLabel">
                Modal title
              </h1>
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div class="modal-body">...</div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button type="button" class="btn btn-primary">
                Understood
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="input-group mb-3">
        <span className="input-group-text">Add Task</span>
        <input
          className="form-control"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              addTodo();
            }
          }}
        />
        <button className="btn btn-outline-primary" onClick={addTodo}>
          Add
        </button>
      </div>

      <table className="table" style={{ width: "100%", tableLayout: "fixed" }}>
        <colgroup>
          {/* Cột đầu tiên: ID */}
          <col style={{ width: "10%" }} />
          {/* Cột thứ hai: Task */}
          <col style={{ width: "40%" }} />
          {/* Cột spacer: tự động chiếm không gian còn lại */}
          <col style={{ width: "auto" }} />
          {/* Cột cuối: Edit/Delete */}
          <col style={{ width: "20%" }} />
        </colgroup>
        <thead>
          <tr className="table-success">
            <th scope="col">#</th>
            <th scope="col">Task</th>
            <th scope="col"></th> {/* Cột spacer không có tiêu đề */}
            <th scope="col">Edit/Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos == null ? (
            <></>
          ) : (
            todos.map((todo) => (
              <Todo
                key={todo.id}
                todo={todo}
                toggleStatus={toggleStatus}
                deleteTodo={deleteTodo}
              />
            ))
          )}
        </tbody>
      </table>
      <script
        src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js"
        integrity="sha384-YvpcrYf0tY3lHB60NNkmXc5s9fDVZLESaAA55NDzOxhy9GkcIdslK1eN7N6jIeHz"
        crossorigin="anonymous"
      ></script>
    </>
  );
}
export default Todolist;
