import React, { useState, useEffect } from "react";
import axios from "axios";
import bootstrap from "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    axios.get("http://localhost:8080/todos").then( (res) => setTodos(res.data));
  }, []);

  const addTodo = () => {
    axios.post("http://localhost:8080/todos", { title, status: false }).then((res) => {
      setTodos([...todos, res.data]);
      setTitle("");
    });
  };

  const toggleStatus = (id, status) => {
    axios.put(`http://localhost:8080/todos/${id}`, { status: !status }).then(() => {
      setTodos(todos.map(todo => (todo.ID === id ? { ...todo, status: !status } : todo)));
    });
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:8080/todos/${id}`).then(() => {
      setTodos(todos.filter(todo => todo.ID !== id));
    });
  };

  return (
    <div>
      <div className="container">
      <h1>To-Do List</h1>
        <input className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} />
        <button className="btn btn-outline-primary m-5" onClick={addTodo}>Add</button>
        <ul className="list-group">
          {todos.map((todo) => (
            <li  key={todo.ID}>
              <span style={{ textDecoration: todo.status ? "line-through" : "none" }}>
                {todo.title}
              </span>
              <button className="btn btn-outline-primary m-5" onClick={() => toggleStatus(todo.ID, todo.status)}>
                {todo.status ? "Undo" : "Done"}
              </button>
              <button className="btn btn-outline-primary m-5" onClick={() => deleteTodo(todo.ID)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
