import React, { useState, useEffect } from "react";
import axios from "axios";
import Todo from "../Todo";

function Todolist() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    axios.get("http://localhost:8080/todos").then((res) => setTodos(res.data));
    if (todos == null) {
      console.log("todo is null");
      return <></>;
    } else {
      console.log(todos);
    }
  }, []);

  const addTodo = () => {
    axios
      .post("http://localhost:8080/todos", { title, status: false })
      .then((res) => {
        setTodos([...todos, res.data]);
        setTitle("");
      });
  };

  const toggleStatus = (id, status) => {
    axios
      .put(`http://localhost:8080/todos/${id}`, { status: !status })
      .then(() => {
        setTodos(
          todos.map((todo) =>
            todo.id === id ? { ...todo, status: !status } : todo
          )
        );
      });
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:8080/todos/${id}`).then(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    });
  };

  const myModal = document.getElementById("myModal");
  const myInput = document.getElementById("myInput");

  myModal.addEventListener("shown.bs.modal", () => {
    myInput.focus();
  });


  return (
    <>
    <div class="modal modal-sheet position-static d-block bg-body-secondary p-4 py-md-5" tabindex="-1" role="dialog" id="modalChoice">
  <div class="modal-dialog" role="document">
    <div class="modal-content rounded-3 shadow">
      <div class="modal-body p-4 text-center">
        <h5 class="mb-0">Enable this setting?</h5>
        <p class="mb-0">You can always change your mind in your account settings.</p>
      </div>
      <div class="modal-footer flex-nowrap p-0">
        <button type="button" class="btn btn-lg btn-link fs-6 text-decoration-none col-6 py-3 m-0 rounded-0 border-end"><strong>Yes, enable</strong></button>
        <button type="button" class="btn btn-lg btn-link fs-6 text-decoration-none col-6 py-3 m-0 rounded-0" data-bs-dismiss="modal">No thanks</button>
      </div>
    </div>
  </div>
</div>
      <div className="input-group mb-3">
        <span className="input-group-text">Add Task</span>
        <input
          className="form-control"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              addTodo();
            }
          }}
        />
        <button className="btn btn-outline-primary" onClick={addTodo}>
          Add
        </button>
      </div>

      <table className="table" style={{ width: "100%", tableLayout: "fixed" }}>
        <colgroup>
          {/* Cột đầu tiên: ID */}
          <col style={{ width: "10%" }} />
          {/* Cột thứ hai: Task */}
          <col style={{ width: "40%" }} />
          {/* Cột spacer: tự động chiếm không gian còn lại */}
          <col style={{ width: "auto" }} />
          {/* Cột cuối: Edit/Delete */}
          <col style={{ width: "20%" }} />
        </colgroup>
        <thead>
          <tr className="table-success">
            <th scope="col">#</th>
            <th scope="col">Task</th>
            <th scope="col"></th> {/* Cột spacer không có tiêu đề */}
            <th scope="col">Edit/Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos == null ? (
            <></>
          ) : (
            todos.map((todo) => (
              <Todo
                key={todo.id}
                todo={todo}
                toggleStatus={toggleStatus}
                deleteTodo={deleteTodo}
              />
            ))
          )}
        </tbody>
      </table>
    </>
  );
}
export default Todolist;
