import React, { useState, useEffect } from "react";
import axios from "axios";
import Todo from "../Todo";

function Todolist() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");

  useEffect(() => {
    axios.get("http://localhost:8080/todos").then((res) => setTodos(res.data));
    if (todos == null) {
      console.log("todo is null");
      return <></>;
    } else {
      console.log(todos);
    }
  }, []);

  const addTodo = () => {
    axios
      .post("http://localhost:8080/todos", { title, status: false })
      .then((res) => {
        setTodos([...todos, res.data]);
        setTitle("");
      });
  };

  const toggleStatus = (id, status) => {
    axios
      .put(`http://localhost:8080/todos/${id}`, { status: !status })
      .then(() => {
        setTodos(
          todos.map((todo) =>
            todo.id === id ? { ...todo, status: !status } : todo
          )
        );
      });
  };

  const deleteTodo = (id) => {
    axios.delete(`http://localhost:8080/todos/${id}`).then(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    });
  };

  return (
    <>
      <div className="input-group mb-3">
        <input
          className="form-control"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              addTodo();
            }
          }}
        />
        <button className="btn btn-outline-primary" onClick={addTodo}>
          Add
        </button>
      </div>

      <table class="table">
        <thead class="thead-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Task</th>
            <th scope="col">Edit/Delete</th>
          </tr>
        </thead>
        <tbody>
          {todos == null ? (
            <></>
          ) : (
            todos.map((todo) => (
              <Todo
                key={todo.id}
                todo={todo}
                toggleStatus={toggleStatus}
                deleteTodo={deleteTodo}
              />
            ))
          )}
        </tbody>
      </table>

      <div className="justify-content-between">
        <ul className="list-group">
          {todos == null ? (
            <></>
          ) : (
            todos.map((todo) => (
              <Todo
                key={todo.id}
                todo={todo}
                toggleStatus={toggleStatus}
                deleteTodo={deleteTodo}
              />
            ))
          )}
        </ul>
      </div>
    </>
  );
}
export default Todolist;
